routines.routine:
if (dobj == player)
{
     println("Not physically possible.");
}

if (dobj.portable == 1)
{
     if (dobj.holder != player)
     {
          println("(Taken)");
          dobj.holder = player;
     }
}

if (airlock1Open == 0)
{
     println("A computerized voice says, 'WARNING: DEPRESSSURIZING AIRLOCK'");
     println("The exterior door to the west opens and the interrior door to east closes.");
     airlock1Open = 1;
     airlock_1.w = lunar_surface;
     airlock_1.e = 255;
}
else
{
     println("A computerized voice says, 'RESTORING ATMOSPHERIC PRESSURE'");
     println("The exterior door to the west closes and the interrior door to east opens.");
     airlock1Open = 0;
     airlock_1.w = 255;
     airlock_1.e = 2;
}

println("The turbo lift descends to the ground level of the station.");
println("The door to the west opens, revealing a corridor.");
player.holder = turbo_lift_1;

println("The turbo lift rises to the top level of the station.");
println("The door to the west opens, revealing a command center.");
player.holder = turbo_lift_2;

println("The slug explodes in a violent maelstrom of squishy goo.");
slugKilled = 1;
score = score + 20;
giant_space_slug.holder = offscreen;
small_white_cube.holder = offscreen;

if (airlock2Open == 0)
{
     println("A computerized voice says, 'WARNING: DEPRESSSURIZING AIRLOCK'");
     println("The exterior door to the east opens and the interrior door to west closes.");
     airlock2Open = 1;
     airlock_2.e = lunar_surface;
     airlock_1.w = 255;
}
else
{
     println("A computerized voice says, 'RESTORING ATMOSPHERIC PRESSURE'");
     println("The exterior door to the east closes and the interrior door to west opens.");
     airlock2Open = 0;
     airlock_2.w = 11;
     airlock_2.e = 255;
}

if (radioWorks == 1)
{
     println("Using the radio you are able to contact Earth. Though a rescue mission will take some to mount, you will have plenty of space slug meat to keep you fed.");
     println("***CONGRATULATIONS***");
     score = 100;
}
else
{
     println("The radio beeps and displays the message 'COMM BOARD FAILURE'.");
}

if (player.holder == junction && giant_space_slug.holder == junction)
{
     println("The highly toxic slug blocks your way.");
}

else if (player.holder == airlock_1 && airlock_1.e == 255)
{
     println("The inner airlock door is closed.");
}
else if (player.holder == airlock_2 && airlock_2.e == 255)
{
     println("The outer airlock door is closed.");
}
else
{
     move();
}

if (space_suit.beingworn == 0)
{
     println("Oh, wait.  Did I mention the slug was highly toxic?  I think I did.");
     println("Touching the slug results in severe hallucinations.");
     println("You wake up face down in your quarters.");
     player.holder = quarters;
}
else
{
     println("YEEEEEHAAAAAA.");
     println("That was fun.");
}

if (player.holder  == airlock_1 && airlock_1.w == 255)
{
     println("The exterior airlock door is closed.");
}
else if (player.holder  == airlock_2 && airlock_2.w == 255)
{
     println("The interior airlock door is closed.");
}
else
{
     move();
}

events.event:
if  (  (player.holder == airlock_1 && airlock_1.w != 255) || (player.holder == airlock_2 && airlock_1.e != 255))
{
     if (space_suit.beingworn == 0)
     {
          println("***YOU HAVE SUFFOCATED***");
          player.holder = 2;
          space_suit.holder = storage_locker;
          airlock1Open = 0;
          airlock2Open = 1;
          airlock_1.w = 255;
          airlock_1.e = 2;
          airlock_2.w = 255;
          airlock_2.e = lunar_surface;
          look();
     }
}

if (player.holder == 11 && slugKilled == 0)
{
     giant_space_slug.holder = junction;
     println("A computerized voice says, 'WARNING: UNAUTHORIZED ACCESS TO COMMAND STATION'");
}
