routines.routine:
if (player.holder == TOP_OF_DEBRIS)
{
     println("AS YOU SQUIRM THROUGH THE NARROW GAP, THE ROCKS GIVE WAY CAUSING YOU TO FALL MANY FEET TO THE TUNNEL FLOOR.");
}

if (player.holder == TOP_OF_SHAFT)
{
     if (wooden_crate.holder==player)
     {
          println("YOU CAN'T FIT THROUGH THE OPENING WHILE CARRYING THE CRATE.");
     }
	else
     {
          move();
     }
}
else
{
     move();
}

if (paperclip.holder == player)
{
     if (lockPicked== 0)
     {
          println("AFTER SEVERAL ATTEMPTS, YOU MANAGE TO UNLOCK AND OPEN THE DOOR.");
          door.open=true;
          door.openable=true;
          door.locked=false;
          door.lockable=false;
          score = score + 20;
          lockPicked=1;
     }
     else
     {
          println("THE DOOR IS ALREADY OPEN.");
     }
}
else
{
     println("YOU HAVE NOTHING TO DO THAT WITH.");
}

if (cigarette_lighter.holder == player)
{
     if (pile_of_leaves.holder != player)
     {
          if (player.holder == base_of_shaft)
          {
               println("FUELED BY THE DRAFT FROM ABOVE, THE LEAVES EVAPORATE IN CLOUD OF ACRID SMOKE WHICH RISES QUICKLY UP THE SHAFT.");
               score = score + 20;
               on_ladder.up=top_of_shaft;
               burnedLeaves=1;
               pile_of_leaves.holder=offscreen;
          }
          else if (player.holder == on_ladder)
          {
               println("YOU CAN'T DO THAT WHILE ON A LADDER");
          }
          else
          {
               println("THE LEAVES SMOLDER A BIT, THEN GO OUT DUE TO THE HIGH HUMIDITY.");
          }
     }
     else
     {
          println("THE LEAVES (WHICH YOU ARE CARRYING) IGNTIE. UNFORTUNATELY, THE FIRE RAPIDLY SPREADS TO YOU.");
          println("NEXT TIME TRY DROPPING THE LEAVES.");
          call kill_player;
     }
}
else
{
     println("YOU HAVE NO IGNITION SOURCE.");
}

println("AS YOU RELUCTANTLY PICK UP THE PAPPERS, THEY DISINTEGRATE, LEAVING YOU WITH A BLACKENED PAPERCLIP.");
pile_of_gunk.holder=offscreen;
paperclip.holder=player;
score = score + 5;

if (flashlight.lit==1)
{
     println("It's already on.");
}
else
{
     println("Click.");
     flashlight.lit=1;
     look();
}

if (flashlight.lit==0)
{
     println("IT'S ALREADY OFF.");
     flashlight.lit=0;
     look();
}
else
{
     println("CLICK.");
     flashlight.lit=0;
}

if (paperclip.holder==offscreen)
{
     println("THE REMAINING WATER IN THE TANK WASHES AWAY THE GUNK, LEAVING A PAPERCLIP AT THE BOTTOM OF THE BOWL.");
     paperclip.holder=filthy_toilet;
     pile_of_gunk.holder=offscreen;
     score = score + 5;
}
else
{
     println("NOTHING HAPPENS.");
}

if (wrench.holder == player)
{
     if (player.holder == top_of_shaft)
     {
          if (cupola.holder==top_of_shaft)
          {
               println("WITH SOME DIFFICULTY, YOU MANAGE TO REMOVE THE BOLTS. AS YOU PUSH THE CUPOLA UP, IT FALLS OUT OF THE WAY.");
               cupola.holder=0;
               cupola.initial_description="THERE IS A HEAVY, METAL CUPOLA ON THE GROUND.";
               cupola.description="THE HEAVY METAL LID HAS STARTED RUSTING WITH AGE.";
               top_of_shaft.description="YOU ARE AT THE TOP OF A VENTILATION SHAFT. ABOVE, AN OPEN HOLE LEADS OUTSIDE.";
               cupola.scenery=0;
               top_of_shaft.up = top_of_hill;
               score = score + 10;
          }
          else
          {
               println("YOU'VE ALREADY DONE THAT.");
          }
     }
     else
     {
          println("YOU CAN'T DO THAT HERE.");
     }
}
else
{
     println("YOU DON'T HAVE ANYTHING TO DO THAT WITH.");
}

if (player.holder==top_of_shaft)
{
     println("FOR SOME INEXPLICABLE REASON YOU DECIDE TO JUMP OFF THE LADDER.");
     println("UNFORTUNATELY, THE ROCK FLOOR AT THE BOTTOM OF THE SHAFT ISN'T QUITE AS FORGIVING AS YOU HAD ANTICIPATED.");
     call kill_player;
}
else if (player.holder==on_ladder)
{
     println("YOU LAND WITH A THUD AT THE BOTTOM OF THE SHAFT.");
     player.holder = base_of_shaft;
}
else
{
     println("WHEEEE!");
}

println("***YOU HAVE DIED***");
player.holder=2;
flashlight.holder=1;
look();

if (player.holder == top_of_shaft)
{
     println("THE CRATE FALLS DOWN THE SHAFT AND SMASHES AT THE BOTTOM.");
     wooden_crate.holder=offscreen;
     splintered_wood.holder=base_of_shaft;
     gold_bars.holder=base_of_shaft;
}
else if (player.holder == on_ladder)
{
     println("THE CRATE FALLS DOWN THE SHAFT, NEARLY SMASHING.");
     wooden_crate.holder=base_of_shaft;
}

if (pair_of_gloves.beingworn == 1)
{
     println("THE COIN SNAPS NEATLY INTO THE RECESS.");
     println("THROUGH THE CAVERNS, YOU CAN HEAR THE DISTANT SOUND OF PUMPS RUNNING.");
     flooded_tunnel.nw=base_of_shaft;
     flooded_tunnel.w=255;
     flooded_tunnel.sw=radio_works;
     volt_meter.description = "THE NEEDLE INDICATES CURRENT IS FLOWING.";
     flooded_tunnel.description = "THIS IS A LOW POINT AT THE END OF AN E-W TUNNEL. THE MAIN TUNNEL LEADS EAST. SMALLER PASSAGES LEAD NORTHWEST AND SOUTHWEST.";
     score = score + 20;
     coin.portable=0;
     coin.holder = small_round_recess;
     water.holder=offscreen;
}
else
{
     println("***ZAP***");
     println("A STRONG ELECTRICAL SHOCK KNOCKS YOU BACK.");
}

if (treeDown == 0)
{
     println("AFTER SEVERAL HARD SHOVES, THE TREE TOPPLES OVER, TAKING OUT A SECTION OF THE WIRE LARGE ENOUGH TO PASS THROUGH.");
     57.n =overgrown_road;
     treeDown=1;
     rotting_tree.initial_description = "A ROTTING TREE LIES ON THE GROUND.";
     57.description = "YOUR ARE STANDING IN THE MIDST OF DENSE FOREST.  WHAT'S LEFT OF A BARBED WIRE FENCE, NO LONGER BLOCKS THE WAY NORTH.";
     score = score + 5;
}
else
{
     println("THE TREE IS ALREADY DOWN.");
}

if (treeDown == 1)
{
     println("THE TREE IS ALREADY DOWN.");
}
else
{
     println("THE TRUNK DOESN'T HAVE ANY BRANCHES SUITABLE FOR CLIMBING.");
}

if (gotGold==0)
{
     score = score + 20;
     gotGold=1;
}

if (dobj.portable == 1)
{
     if (dobj.holder != PLAYER)
     {
          println("(TAKEN)");
          dobj.holder = PLAYER;
     }
}

if (wrench.holder != PLAYER)
{
     println("THE CUPOLA IS BOLTED SHUT");
}
else
{
     println("(WITH THE WRENCH)");
     println("AFTER REMOVING THE BOLTS, YOU PUSH THE CUPOLA UP OUT OF THE WAY");
     top_of_shaft.up = top_of_hill;
     cupola.holder = offscreen;
     bolts.holder = offscreen;
}

if (dobj != wooden_crate)
{
     if (player.holder == top_of_shaft)
     {
          println("IT FALLS TO THE BOTTOM OF THE SHAFT.");
          dobj.holder = base_of_shaft;
     }
     if (player.holder == on_ladder)
     {
          println("IT FALLS TO THE BOTTOM OF THE SHAFT.");
          dobj.holder = base_of_shaft;
     }
}

events.event:
if ( player.holder == base_of_shaft )
{
     if (on_ladder.up != top_of_shaft)
     {
          println("HIGH ABOVE, YOU CAN HEAR BUZZING.");
     }
}

if (player.holder == TOP_OF_HILL)
{
     if (gameOver == 0)
     {
          if (score == 100)
          {
               gameOver =1 ;
               println("CONGRATULATIONS.  YOU'VE COMPLETED THIS ADVENTURE!");
               println("TYPE 'QUIT' TO QUIT.");
          }
          else
          {
               println("CONGRATULATIONS.  YOU'VE ESCAPED BUT DON'T HAVE A FULL SCORE.");
          }
     }
}

if (turnsWithoutLight == 4)
{
     println("***CRASH***");
     println("IT APPEARS THE CAVERNS HAVE COLLAPSED ON YOU.");
     println("I SUPPOSE THAT'S WHAT HAPPENS WHEN YOU EXPLORE WITHOUT A LIGHT SOURCE.");
     println("LET'S TRY THIS ADVENTURE AGAIN.");
     player.holder = dirt_road;
     flashlight.holder = dirt_road;
     look();
}
